import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import ProductList from './components/ProductList';
import OrderForm from './components/OrderForm';
import AdminPanel from './components/AdminPanel';
import About from './components/About';
import { FaHome, FaShoppingCart, FaInfoCircle, FaSearch } from 'react-icons/fa';

interface CartItem {
  id: number;
  name: string;
  price: number;
  quantity: number;
}

const App: React.FC = () => {
  const [cart, setCart] = useState<CartItem[]>([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [currentSlide, setCurrentSlide] = useState(0);
  const slides = [
    'https://images.unsplash.com/photo-1600585154340-be6161a56a0c', // Сад
    'https://images.unsplash.com/photo-1506947411487-5ebfa7e723d5', // Огород
    'https://images.unsplash.com/photo-1515150144380-b230a6b6c915', // Растения
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 3000);
    return () => clearInterval(interval);
  }, [slides.length]);

  const addToCart = (product: { id: number; name: string; price: number; quantity: number }) => {
    setCart(prev => {
      const existingItem = prev.find(item => item.id === product.id);
      if (existingItem) {
        return prev.map(item =>
          item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      }
      return [...prev, { ...product, quantity: 1 }];
    });
  };

  const clearCart = () => setCart([]);

  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    window.location.href = `/catalog?search=${searchQuery}`;
  };

  return (
    <Router>
      <div className="min-h-screen bg-gray-100 flex flex-col">
        {/* Шапка с адаптивной навигацией */}
        <header className="bg-white shadow-md py-4">
          <div className="container mx-auto px-4 flex flex-col items-center justify-between md:flex-row">
            <h1 className="text-3xl font-extrabold text-gray-800 mb-4 md:mb-0 drop-shadow-md md:text-4xl">
              <span className="text-green-600">У</span>
              <span className="text-orange-500">Д</span>ача
            </h1>
            <nav className="flex flex-col items-center space-y-2 md:flex-row md:space-y-0 md:space-x-6 w-full md:w-auto">
              <Link to="/" className="text-gray-600 hover:text-green-600 flex items-center transition-colors text-sm md:text-base">
                {/* @ts-ignore */}
                <FaHome className="mr-1" /> Главная
              </Link>
              <Link to="/catalog" className="text-gray-600 hover:text-green-600 flex items-center transition-colors text-sm md:text-base">
                {/* @ts-ignore */}
                <FaShoppingCart className="mr-1" /> Каталог
              </Link>
              <Link
                to="/order"
                className="relative bg-green-600 text-white px-3 py-2 rounded-lg hover:bg-green-700 flex items-center transition-transform transform hover:scale-105 shadow-md text-sm md:text-base md:px-4"
              >
                {/* @ts-ignore */}
                <FaShoppingCart className="mr-2" />
                Корзина
                {cart.length > 0 && (
                  <span className="absolute -top-2 -right-2 bg-red-500 text-white text-xs rounded-full h-5 w-5 flex items-center justify-center">
                    {cart.length}
                  </span>
                )}
              </Link>
              <Link to="/about" className="text-gray-600 hover:text-green-600 flex items-center transition-colors text-sm md:text-base">
                {/* @ts-ignore */}
                <FaInfoCircle className="mr-1" /> О нас
              </Link>
              <form onSubmit={handleSearch} className="flex items-center w-full md:w-auto">
                <input
                  type="text"
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  placeholder="Поиск..."
                  className="p-2 border rounded-l-lg bg-gray-50 text-gray-700 focus:outline-none focus:ring-2 focus:ring-green-600 w-full md:w-40"
                />
                <button type="submit" className="p-2 bg-gray-200 text-gray-600 rounded-r-lg hover:bg-gray-300">
                  {/* @ts-ignore */}
                  <FaSearch />
                </button>
              </form>
            </nav>
          </div>
        </header>

        {/* Баннер с адаптивной высотой */}
        <section className="bg-gray-200 py-8 md:py-16 text-center">
          <div className="relative w-full h-48 md:h-64">
            {slides.map((slide, index) => (
              <img
                key={index}
                src={slide}
                alt={`Slide ${index}`}
                className={`absolute top-0 left-0 w-full h-full object-cover transition-opacity duration-1000 ${index === currentSlide ? 'opacity-100' : 'opacity-0'}`}
              />
            ))}
            <div className="absolute inset-0 flex flex-col items-center justify-center text-gray-800">
              <div className="bg-white bg-opacity-80 p-3 rounded-lg shadow-md md:p-4">
                <h2 className="text-2xl font-bold mb-2 drop-shadow-lg md:text-4xl">Всё для вашего сада!</h2>
                <p className="text-base text-gray-700 drop-shadow-md md:text-lg">Семена, инструменты и удобрения в УДача</p>
              </div>
            </div>
          </div>
        </section>

        {/* Основной контент */}
        <main className="container mx-auto px-4 py-8 md:px-6 md:py-12 flex-1 bg-gray-100">
          <Routes>
            <Route path="/" element={<ProductList addToCart={addToCart} cart={cart} clearCart={clearCart} isPopular />} />
            <Route path="/catalog" element={<ProductList addToCart={addToCart} cart={cart} clearCart={clearCart} />} />
            <Route path="/order" element={<OrderForm cart={cart} clearCart={clearCart} />} />
            <Route path="/admin" element={<AdminPanel />} />
            <Route path="/about" element={<About />} />
          </Routes>

          {/* Специальные предложения с адаптивной сеткой */}
          <section className="mt-8 md:mt-16">
            <h2 className="text-xl font-semibold text-gray-700 mb-4 md:text-2xl md:mb-6">Специальные предложения</h2>
            <div className="grid grid-cols-1 gap-4 md:grid-cols-3 md:gap-6">
              <div className="bg-white p-4 rounded-lg shadow-md hover:shadow-lg transition-shadow md:p-6">
                <img src="https://via.placeholder.com/300x150" alt="Акция" className="w-full h-32 object-cover rounded-t-lg mb-4 md:h-40" />
                <h3 className="text-lg font-semibold text-gray-800 md:text-xl">Скидка 20%</h3>
                <p className="text-gray-600 text-sm md:text-base">На все семена до конца месяца</p>
              </div>
              <div className="bg-white p-4 rounded-lg shadow-md hover:shadow-lg transition-shadow md:p-6">
                <img src="https://via.placeholder.com/300x150" alt="Акция" className="w-full h-32 object-cover rounded-t-lg mb-4 md:h-40" />
                <h3 className="text-lg font-semibold text-gray-800 md:text-xl">2+1 бесплатно</h3>
                <p className="text-gray-600 text-sm md:text-base">Третий пакет семян в подарок</p>
              </div>
              <div className="bg-white p-4 rounded-lg shadow-md hover:shadow-lg transition-shadow md:p-6">
                <img src="https://via.placeholder.com/300x150" alt="Акция" className="w-full h-32 object-cover rounded-t-lg mb-4 md:h-40" />
                <h3 className="text-lg font-semibold text-gray-800 md:text-xl">Скидка на самовывоз</h3>
                <p className="text-gray-600 text-sm md:text-base">При заказе от 1000 руб.</p>
              </div>
            </div>
          </section>
        </main>

        {/* Подвал */}
        <footer className="bg-gray-700 text-gray-200 py-6 md:py-8">
          <div className="container mx-auto px-4 text-center md:px-6">
            <p className="text-sm md:text-base">© 2025 УДача. Все права защищены.</p>
            <p className="mt-2 text-xs md:text-sm">г. Михайловск, заезд Климова 41/2 | Телефон: +7 (999) 123-45-67</p>
          </div>
        </footer>
      </div>
    </Router>
  );
};

export default App;
