import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import ProductList from './components/ProductList';
import OrderForm from './components/OrderForm';
import AdminPanel from './components/AdminPanel';
import About from './components/About';
import { FaHome, FaShoppingCart, FaInfoCircle, FaSearch } from 'react-icons/fa';
import { IconType } from 'react-icons';

interface CartItem {
  id: number;
  name: string;
  price: number;
  quantity: number;
}

const TypedFaHome: IconType = FaHome;
const TypedFaShoppingCart: IconType = FaShoppingCart;
const TypedFaInfoCircle: IconType = FaInfoCircle;
const TypedFaSearch: IconType = FaSearch;

const App: React.FC = () => {
  const [cart, setCart] = useState<CartItem[]>([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [currentSlide, setCurrentSlide] = useState(0);
  const slides = [
    'https://images.unsplash.com/photo-1597227774093-7e7e0e0eabd5',
    'https://images.unsplash.com/photo-1600585154340-be6161a56a0c',
    'https://images.unsplash.com/photo-1593795899630-2a565d76d216',
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 3000);
    return () => clearInterval(interval);
  }, [slides.length]);

  const addToCart = (product: { id: number; name: string; price: number; quantity: number }) => {
    setCart(prev => {
      const existingItem = prev.find(item => item.id === product.id);
      if (existingItem) {
        return prev.map(item =>
          item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      }
      return [...prev, { ...product, quantity: 1 }];
    });
  };

  const clearCart = () => setCart([]);

  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    window.location.href = `/catalog?search=${searchQuery}`;
  };

  return (
    <Router>
      <div className="min-h-screen bg-gray-50 flex flex-col">
        <header className="bg-white shadow-md">
          <div className="container mx-auto px-6 py-4 flex justify-between items-center">
            <h1 className="text-2xl font-semibold text-gray-800">УДача</h1>
            <nav className="flex items-center space-x-6">
              <Link to="/" className="text-gray-600 hover:text-green-600 flex items-center">
                <TypedFaHome className="mr-1" /> Главная
              </Link>
              <Link to="/catalog" className="text-gray-600 hover:text-green-600 flex items-center">
                <TypedFaShoppingCart className="mr-1" /> Каталог
              </Link>
              <Link to="/order" className="text-gray-600 hover:text-green-600 flex items-center">
                <TypedFaShoppingCart className="mr-1" /> Корзина ({cart.length})
              </Link>
              <Link to="/about" className="text-gray-600 hover:text-green-600 flex items-center">
                <TypedFaInfoCircle className="mr-1" /> О нас
              </Link>
              <form onSubmit={handleSearch} className="flex items-center">
                <input
                  type="text"
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  placeholder="Поиск товаров..."
                  className="p-2 border rounded-l-lg focus:outline-none focus:ring-2 focus:ring-green-600"
                />
                <button type="submit" className="p-2 bg-green-600 text-white rounded-r-lg hover:bg-green-700">
                  <TypedFaSearch />
                </button>
              </form>
            </nav>
          </div>
        </header>

        <section className="bg-green-100 py-16 text-center">
          <div className="relative w-full h-64">
            {slides.map((slide, index) => (
              <img
                key={index}
                src={slide}
                alt={`Slide ${index}`}
                className={`absolute top-0 left-0 w-full h-full object-cover transition-opacity duration-1000 ${index === currentSlide ? 'opacity-100' : 'opacity-0'}`}
              />
            ))}
            <div className="absolute inset-0 flex flex-col items-center justify-center text-white bg-black bg-opacity-50">
              <h2 className="text-4xl font-bold mb-4">Всё для вашего сада!</h2>
              <p className="text-lg">Семена, инструменты и удобрения в УДача</p>
            </div>
          </div>
        </section>

        <main className="container mx-auto px-6 py-12 flex-1">
          <Routes>
            <Route path="/" element={<ProductList addToCart={addToCart} cart={cart} clearCart={clearCart} isPopular />} />
            <Route path="/catalog" element={<ProductList addToCart={addToCart} cart={cart} clearCart={clearCart} />} />
            <Route path="/order" element={<OrderForm cart={cart} clearCart={clearCart} />} />
            <Route path="/admin" element={<AdminPanel />} />
            <Route path="/about" element={<About />} />
          </Routes>

          <section className="mt-16">
            <h2 className="text-2xl font-semibold text-gray-800 mb-6">Специальные предложения</h2>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <div className="bg-white p-6 rounded-lg shadow-md">
                <img src="https://via.placeholder.com/300x150" alt="Акция" className="w-full h-40 object-cover rounded-t-lg mb-4" />
                <h3 className="text-xl font-semibold text-gray-800">Скидка 20%</h3>
                <p className="text-gray-600">На все семена до конца месяца</p>
              </div>
              <div className="bg-white p-6 rounded-lg shadow-md">
                <img src="https://via.placeholder.com/300x150" alt="Акция" className="w-full h-40 object-cover rounded-t-lg mb-4" />
                <h3 className="text-xl font-semibold text-gray-800">2+1 бесплатно</h3>
                <p className="text-gray-600">Третий пакет семян в подарок</p>
              </div>
              <div className="bg-white p-6 rounded-lg shadow-md">
                <img src="https://via.placeholder.com/300x150" alt="Акция" className="w-full h-40 object-cover rounded-t-lg mb-4" />
                <h3 className="text-xl font-semibold text-gray-800">Скидка на самовывоз</h3>
                <p className="text-gray-600">При заказе от 1000 руб.</p>
              </div>
            </div>
          </section>
        </main>

        <footer className="bg-gray-800 text-white py-8">
          <div className="container mx-auto px-6 text-center">
            <p>© 2025 УДача. Все права защищены.</p>
            <p className="mt-2 text-sm">г. Михайловск, заезд Климова 41/2 | Телефон: +7 (999) 123-45-67</p>
          </div>
        </footer>
      </div>
    </Router>
  );
};

export default App;
