import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useLocation } from 'react-router-dom';

interface CartItem {
  id: number;
  name: string;
  price: number;
  quantity: number;
}

const OrderForm: React.FC = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const cart = (location.state as { cart?: CartItem[] })?.cart || [];

  const [customerName, setCustomerName] = useState('');
  const [phone, setPhone] = useState('');
  const [deliveryMethod, setDeliveryMethod] = useState<'pickup' | 'delivery'>('pickup');
  const [address, setAddress] = useState('');
  const [desiredDate, setDesiredDate] = useState<string>('');
  const [desiredTime, setDesiredTime] = useState<string>('');
  const [error, setError] = useState<string | null>(null);

  const availableDates = () => {
    const dates = [];
    const today = new Date();
    for (let i = 0; i < 7; i++) {
      const date = new Date(today);
      date.setDate(today.getDate() + i);
      if (date.getDay() !== 1) { // Исключаем понедельник
        dates.push(date.toISOString().split('T')[0]); // Формат YYYY-MM-DD
      }
    }
    return dates;
  };

  const availableTimes = ['09:00', '10:00', '11:00', '12:00', '13:00', '14:00', '15:00', '16:00'];

  const validateDateTime = () => {
    if (!desiredDate || !desiredTime) return false;
    const now = new Date();
    const selectedDateTime = new Date(`${desiredDate}T${desiredTime}:00`);
    const minTime = new Date(now.getTime() + 30 * 60000); // +30 минут
    return selectedDateTime >= minTime;
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (cart.length === 0) {
      setError('Добавьте товары в корзину');
      return;
    }
    if (!validateDateTime()) {
      setError('Выберите дату и время не раньше чем через 30 минут');
      return;
    }

    const orderData = {
      customer_name: customerName,
      phone,
      items: cart.map(item => ({ id: item.id, qty: item.quantity })),
      delivery_method: deliveryMethod,
      desired_datetime: `${desiredDate} ${desiredTime}:00`,
      ...(deliveryMethod === 'delivery' && { address }),
    };

    axios.post('http://localhost:5000/api/orders', orderData)
      .then(() => {
        alert('Заказ успешно отправлен!');
        navigate('/');
      })
      .catch(error => {
        setError(error.response?.data?.error || 'Ошибка при отправке заказа');
      });
  };

  return (
    <div className="max-w-2xl mx-auto bg-white p-8 rounded-lg shadow-md">
      <h2 className="section-title text-center">Оформление заказа</h2>
      {cart.length > 0 ? (
        <div className="mb-6">
          <h3 className="text-lg font-semibold text-gray-800 mb-2">Ваша корзина:</h3>
          <ul className="space-y-2">
            {cart.map(item => (
              <li key={item.id} className="text-gray-700">
                {item.name} - {item.quantity} шт. ({item.price * item.quantity} руб.)
              </li>
            ))}
          </ul>
          <p className="mt-4 font-semibold text-gray-800">
            Итого: {cart.reduce((sum, item) => sum + item.price * item.quantity, 0)} руб.
          </p>
        </div>
      ) : (
        <p className="text-red-600 mb-6 text-center">Корзина пуста</p>
      )}
      {error && <p className="text-red-600 mb-6 text-center">{error}</p>}
      <form onSubmit={handleSubmit} className="space-y-6">
        <div>
          <label className="block text-gray-700 font-medium mb-1">Имя</label>
          <input
            type="text"
            value={customerName}
            onChange={(e) => setCustomerName(e.target.value)}
            className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-green-600"
            required
          />
        </div>
        <div>
          <label className="block text-gray-700 font-medium mb-1">Телефон</label>
          <input
            type="tel"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-green-600"
            placeholder="+79991234567"
            required
          />
        </div>
        <div>
          <label className="block text-gray-700 font-medium mb-1">Способ получения</label>
          <select
            value={deliveryMethod}
            onChange={(e) => setDeliveryMethod(e.target.value as 'pickup' | 'delivery')}
            className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-green-600"
          >
            <option value="pickup">Самовывоз</option>
            <option value="delivery">Доставка</option>
          </select>
        </div>
        {deliveryMethod === 'delivery' && (
          <div>
            <label className="block text-gray-700 font-medium mb-1">Адрес (г. Михайловск)</label>
            <input
              type="text"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
              className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-green-600"
              placeholder="г. Михайловск, ул. Ленина, д. 10"
              required
            />
          </div>
        )}
        <div>
          <label className="block text-gray-700 font-medium mb-1">Желаемая дата</label>
          <select
            value={desiredDate}
            onChange={(e) => setDesiredDate(e.target.value)}
            className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-green-600"
            required
          >
            <option value="">Выберите дату</option>
            {availableDates().map(date => (
              <option key={date} value={date}>
                {new Date(date).toLocaleDateString('ru-RU', { day: '2-digit', month: '2-digit', year: 'numeric' })}
              </option>
            ))}
          </select>
        </div>
        <div>
          <label className="block text-gray-700 font-medium mb-1">Желаемое время</label>
          <select
            value={desiredTime}
            onChange={(e) => setDesiredTime(e.target.value)}
            className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-green-600"
            required
          >
            <option value="">Выберите время</option>
            {availableTimes.map(time => (
              <option key={time} value={time}>{time}</option>
            ))}
          </select>
          <p className="text-sm text-gray-500 mt-2">
            Доставка: с 9:00 до 16:00 (кроме понедельника), минимум через 30 минут.
          </p>
        </div>
        <button type="submit" className="w-full btn-primary">
          Отправить заказ
        </button>
      </form>
    </div>
  );
};

export default OrderForm;
