import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

interface Product {
  id: number;
  name: string;
  description: string;
  price: number;
  quantity: number;
  photo: string | null;
  category?: string; // Добавим позже на бэкенде
}

interface CartItem {
  id: number;
  name: string;
  price: number;
  quantity: number;
}

interface ProductListProps {
  addToCart: (product: Product) => void;
  cart: CartItem[];
}

const ProductList: React.FC<ProductListProps> = ({ addToCart, cart }) => {
  const [products, setProducts] = useState<Product[]>([]);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('http://localhost:5000/api/products')
      .then(response => {
        // Временно добавляем категории вручную
        const categorizedProducts = response.data.map((p: Product) => ({
          ...p,
          category: p.name.includes('Семена') ? 'Семена' : 'Инструменты',
        }));
        setProducts(categorizedProducts);
      })
      .catch(error => console.error('Ошибка загрузки товаров:', error));
  }, []);

  const categories = ['Семена', 'Инструменты']; // Пример категорий

  return (
    <div>
      {categories.map(category => (
        <div key={category} className="mb-12">
          <h2 className="text-2xl font-semibold mb-6 text-green-700">{category}</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
            {products
              .filter(product => product.category === category)
              .map(product => (
                <div
                  key={product.id}
                  className="bg-white rounded-lg shadow-md p-6 hover:shadow-xl transition duration-300"
                >
                  {product.photo && (
                    <img
                      src={`http://localhost:5000${product.photo}`}
                      alt={product.name}
                      className="w-full h-48 object-cover rounded-t-lg mb-4"
                    />
                  )}
                  <h3 className="text-lg font-medium text-gray-800">{product.name}</h3>
                  <p className="text-gray-600 text-sm">{product.description}</p>
                  <p className="text-green-600 font-bold mt-2">Цена: {product.price} руб.</p>
                  <p className="text-gray-500 text-sm">В наличии: {product.quantity} шт.</p>
                  <button
                    onClick={() => addToCart(product)}
                    className="mt-4 bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 transition"
                  >
                    Добавить в корзину
                  </button>
                </div>
              ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default ProductList;
