import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useLocation, useNavigate } from 'react-router-dom';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

interface Product {
  id: number;
  name: string;
  price: number;
}

const OrderForm: React.FC = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const selectedProduct = location.state?.selectedProduct as Product | undefined;

  const [customerName, setCustomerName] = useState('');
  const [phone, setPhone] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [deliveryMethod, setDeliveryMethod] = useState<'pickup' | 'delivery'>('pickup');
  const [address, setAddress] = useState('');
  const [desiredDateTime, setDesiredDateTime] = useState<Date | null>(null);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const now = new Date();
    const minTime = new Date(now.getTime() + 30 * 60000); // +30 минут
    setDesiredDateTime(minTime);
  }, []);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!selectedProduct || !desiredDateTime) {
      setError('Выберите товар и желаемое время');
      return;
    }

    const orderData = {
      customer_name: customerName,
      phone,
      items: [{ id: selectedProduct.id, qty: quantity }],
      delivery_method: deliveryMethod,
      desired_datetime: desiredDateTime.toISOString().slice(0, 19).replace('T', ' '),
      ...(deliveryMethod === 'delivery' && { address }),
    };

    axios.post('http://localhost:5000/api/orders', orderData)
      .then(() => {
        alert('Заказ успешно отправлен!');
        navigate('/');
      })
      .catch(error => {
        setError(error.response?.data?.error || 'Ошибка при отправке заказа');
      });
  };

  const filterTime = (time: Date) => {
    const now = new Date();
    const minTime = new Date(now.getTime() + 30 * 60000); // +30 минут
    const hour = time.getHours();
    const minute = time.getMinutes();
    const isMonday = time.getDay() === 1; // 1 = понедельник
    const isValidTime = hour >= 9 && (hour < 16 || (hour === 16 && minute === 0));
    return !isMonday && isValidTime && time >= minTime;
  };

  return (
    <div className="max-w-md mx-auto bg-white p-6 rounded-lg shadow-md">
      <h2 className="text-xl font-semibold mb-4">Оформление заказа</h2>
      {selectedProduct && (
        <div className="mb-4">
          <p className="font-medium">Товар: {selectedProduct.name}</p>
          <p>Цена: {selectedProduct.price} руб.</p>
        </div>
      )}
      {error && <p className="text-red-500 mb-4">{error}</p>}
      <form onSubmit={handleSubmit}>
        <div className="mb-4">
          <label className="block text-gray-700">Имя</label>
          <input
            type="text"
            value={customerName}
            onChange={(e) => setCustomerName(e.target.value)}
            className="w-full p-2 border rounded"
            required
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">Телефон</label>
          <input
            type="tel"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            className="w-full p-2 border rounded"
            placeholder="+79991234567"
            required
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">Количество</label>
          <input
            type="number"
            value={quantity}
            onChange={(e) => setQuantity(Math.max(1, parseInt(e.target.value)))}
            className="w-full p-2 border rounded"
            min="1"
            required
          />
        </div>
        <div className="mb-4">
          <label className="block text-gray-700">Способ получения</label>
          <select
            value={deliveryMethod}
            onChange={(e) => setDeliveryMethod(e.target.value as 'pickup' | 'delivery')}
            className="w-full p-2 border rounded"
          >
            <option value="pickup">Самовывоз</option>
            <option value="delivery">Доставка</option>
          </select>
        </div>
        {deliveryMethod === 'delivery' && (
          <div className="mb-4">
            <label className="block text-gray-700">Адрес (г. Михайловск)</label>
            <input
              type="text"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
              className="w-full p-2 border rounded"
              placeholder="г. Михайловск, ул. Ленина, д. 10"
              required
            />
          </div>
        )}
        <div className="mb-4">
          <label className="block text-gray-700">Желаемое время</label>
          <DatePicker
            selected={desiredDateTime}
            onChange={(date: Date) => setDesiredDateTime(date)}
            showTimeSelect
            timeIntervals={15}
            dateFormat="yyyy-MM-dd HH:mm:ss"
            className="w-full p-2 border rounded"
            filterTime={filterTime}
            minDate={new Date()}
            required
          />
          <p className="text-sm text-gray-500 mt-1">
            Доставка: с 9:00 до 16:00 (кроме понедельника), минимум через 30 минут.
          </p>
        </div>
        <button
          type="submit"
          className="w-full bg-green-500 text-white p-2 rounded hover:bg-green-600 transition"
        >
          Отправить заказ
        </button>
      </form>
    </div>
  );
};

export default OrderForm;
