import React, { useState } from 'react';
import { Routes, Route } from 'react-router-dom';
import ProductList from './components/ProductList';
import OrderForm from './components/OrderForm';

interface CartItem {
  id: number;
  name: string;
  price: number;
  quantity: number;
}

const App: React.FC = () => {
  const [cart, setCart] = useState<CartItem[]>([]);

  const addToCart = (product: { id: number; name: string; price: number; quantity: number }) => {
    setCart(prev => {
      const existingItem = prev.find(item => item.id === product.id);
      if (existingItem) {
        return prev.map(item =>
          item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      }
      return [...prev, { ...product, quantity: 1 }];
    });
  };

  const removeFromCart = (id: number) => {
    setCart(prev => prev.filter(item => item.id !== id));
  };

  const updateCartQuantity = (id: number, quantity: number) => {
    setCart(prev =>
      prev.map(item => (item.id === id ? { ...item, quantity: Math.max(1, quantity) } : item))
    );
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <header className="bg-green-600 text-white p-4 shadow-md">
        <h1 className="text-2xl font-bold">Магазин семян и товаров для дачи</h1>
      </header>
      <main className="container mx-auto p-4">
        <Routes>
          <Route path="/" element={<ProductList addToCart={addToCart} cart={cart} />} />
          <Route
            path="/order"
            element={<OrderForm cart={cart} clearCart={() => setCart([])} />}
          />
        </Routes>
      </main>
      <footer className="bg-green-600 text-white p-4 text-center">
        <p>© 2025 Магазин семян</p>
      </footer>
    </div>
  );
};

export default App;
