import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

interface Product {
  id: number;
  name: string;
  description: string;
  price: number;
  quantity: number;
  photo: string | null;
}

interface CartItem {
  id: number;
  name: string;
  price: number;
  quantity: number;
}

interface ProductListProps {
  addToCart: (product: Product) => void;
  cart: CartItem[];
}

const ProductList: React.FC<ProductListProps> = ({ addToCart, cart }) => {
  const [products, setProducts] = useState<Product[]>([]);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get('http://localhost:5000/api/products')
      .then(response => setProducts(response.data))
      .catch(error => console.error('Ошибка загрузки товаров:', error));
  }, []);

  return (
    <div>
      <h2 className="text-xl font-semibold mb-4">Доступные товары</h2>
      <button
        onClick={() => navigate('/order')}
        className="mb-4 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
      >
        Перейти к корзине ({cart.length} товаров)
      </button>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
        {products.map(product => (
          <div key={product.id} className="bg-white rounded-lg shadow-md p-4 hover:shadow-lg transition">
            {product.photo && (
              <img
                src={`http://localhost:5000${product.photo}`}
                alt={product.name}
                className="w-full h-48 object-cover rounded-t-lg"
              />
            )}
            <h3 className="text-lg font-medium mt-2">{product.name}</h3>
            <p className="text-gray-600">{product.description}</p>
            <p className="text-green-600 font-bold mt-1">Цена: {product.price} руб.</p>
            <p className="text-gray-500">В наличии: {product.quantity} шт.</p>
            <button
              onClick={() => addToCart(product)}
              className="mt-2 bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 transition"
            >
              Добавить в корзину
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductList;
