import React, { useState } from 'react';
import { Routes, Route } from 'react-router-dom';
import ProductList from './components/ProductList';
import OrderForm from './components/OrderForm';

interface CartItem {
  id: number;
  name: string;
  price: number;
  quantity: number;
}

const App: React.FC = () => {
  const [cart, setCart] = useState<CartItem[]>([]);

  const addToCart = (product: { id: number; name: string; price: number; quantity: number }) => {
    setCart(prev => {
      const existingItem = prev.find(item => item.id === product.id);
      if (existingItem) {
        return prev.map(item =>
          item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      }
      return [...prev, { ...product, quantity: 1 }];
    });
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Шапка */}
      <header className="bg-white shadow-md">
        <div className="container mx-auto px-6 py-4 flex justify-between items-center">
          <h1 className="text-2xl font-semibold text-gray-800">Дачный Уголок</h1>
          <div className="flex items-center space-x-4">
            <a href="/" className="text-gray-600 hover:text-green-600">Главная</a>
            <a href="/catalog" className="text-gray-600 hover:text-green-600">Каталог</a>
            <button
              onClick={() => window.location.href = '/order'}
              className="btn-secondary flex items-center"
            >
              <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M3 3h18l-2 12H5L3 3zm4 16a2 2 0 100-4 2 2 0 000 4zm10 0a2 2 0 100-4 2 2 0 000 4z" />
              </svg>
              Корзина ({cart.length})
            </button>
          </div>
        </div>
      </header>

      {/* Баннер */}
      <section className="bg-green-100 py-16 text-center">
        <div className="container mx-auto px-6">
          <h2 className="text-4xl font-bold text-gray-800 mb-4">Всё для вашего сада!</h2>
          <p className="text-lg text-gray-600 mb-6">Семена, инструменты и удобрения с доставкой по Михайловску</p>
          <button className="btn-primary">Посмотреть каталог</button>
        </div>
      </section>

      {/* Основной контент */}
      <main className="container mx-auto px-6 py-12">
        <Routes>
          <Route path="/" element={<ProductList addToCart={addToCart} cart={cart} />} />
          <Route path="/order" element={<OrderForm />} />
        </Routes>

        {/* Акции */}
        <section className="mt-16">
          <h2 className="section-title">Специальные предложения</h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div className="bg-white p-6 rounded-lg shadow-md">
              <img src="https://via.placeholder.com/300x150" alt="Акция" className="w-full h-40 object-cover rounded-t-lg mb-4" />
              <h3 className="text-xl font-semibold text-gray-800">Скидка 20%</h3>
              <p className="text-gray-600">На все семена до конца месяца</p>
            </div>
            <div className="bg-white p-6 rounded-lg shadow-md">
              <img src="https://via.placeholder.com/300x150" alt="Акция" className="w-full h-40 object-cover rounded-t-lg mb-4" />
              <h3 className="text-xl font-semibold text-gray-800">2+1 бесплатно</h3>
              <p className="text-gray-600">Третий пакет семян в подарок</p>
            </div>
            <div className="bg-white p-6 rounded-lg shadow-md">
              <img src="https://via.placeholder.com/300x150" alt="Акция" className="w-full h-40 object-cover rounded-t-lg mb-4" />
              <h3 className="text-xl font-semibold text-gray-800">Бесплатная доставка</h3>
              <p className="text-gray-600">При заказе от 1000 руб.</p>
            </div>
          </div>
        </section>
      </main>

      {/* Подвал */}
      <footer className="bg-gray-800 text-white py-8">
        <div className="container mx-auto px-6 text-center">
          <p>© 2025 Дачный Уголок. Все права защищены.</p>
          <p className="mt-2 text-sm">г. Михайловск | Телефон: +7 (999) 123-45-67</p>
        </div>
      </footer>
    </div>
  );
};

export default App;
